mapper.select:
select *
from user
where username = #{name}
   or email = #{email}
limit 1

mapper.update:
update user
set lastLoginTime = #{time}
where userId = #{userId}

update user
set password = #{password}
where email = #{email}

mapper.select:
select *
from user
where userId = #{userId}
   or githubId = #{githubId}
